declare module '*.css' {
  const content: any; 
  export default content;
}

declare module '*.png' {
  const value: string;
  export default value;
}

// Add other image types if you use them
declare module '*.jpg' {
  const value: string;
  export default value;
}

declare module '*.jpeg' {
  const value: string;
  export default value;
}

declare module '*.svg' {
  const value: string;
  export default value;
}

declare module '*.gif' {
  const value: string;
  export default value;
}